class HtmlIo {
	constructor(game) {
		this.game = game;
		this.game.setIo(this);
		this.$container = $('<div class="htmlio-container">');
		this.mainMatrixDisplay = new MatrixDisplay(10,20);
		this.sideMatrixDisplay = new MatrixDisplay(4,4);
		this.$scoreField = $('<input type="text" class="score" readonly>');
		this.$levelField= $('<input type="text" class="level" readonly>');

		this.setupKeyListener();
		this.setupViewElements();
		this.update();
	}

	beep() {
		(new Audio(
			"data:audio/wav;base64,    "
		)).play();
	}

	setupKeyListener() {
		$('body').keydown(event => {
			const BUTTON_MAP = {
				37: this.game.BTN_LEFT,
				38: this.game.BTN_UP,
				39: this.game.BTN_RIGHT,
				40: this.game.BTN_DOWN
			};

			const virtualButton = BUTTON_MAP[event.which];

			if(virtualButton) this.game.handleButton(virtualButton);
			if(this.game.gameOver) alert("Game Over \nScore: " + this.game.score);
			this.update();
		});
	}



	setupViewElements() {
		this.$container.append(this.mainMatrixDisplay.$table);
		this.$container.append(this.sideMatrixDisplay.$table);
		this.$container.append(this.$levelField);
		this.$container.append(this.$scoreField);
	}

	update() {
		this.mainMatrixDisplay.draw(this.game.map.overlay(this.game.currentBlock, true).getPixels());
		this.sideMatrixDisplay.draw(this.game.nextBlock.getPixels());
		this.$scoreField.val(this.game.score);
		this.$levelField.val(this.game.level + 1);
	}

	displayHighscores(highscorelist){
		let $highscoreContainer = $("<ol></ol>");
		while(highscorelist.length > 0){
			$highscoreContainer.append('<li>'+ highscorelist.pop() +'</li>');
		}
		$(".htmlio-container").remove();
		$("body").append($("<h1>Highscores</h1>"));
		$("body").append($highscoreContainer);
	}
}


const game = new TetrisGame();
const io = new HtmlIo(game);
$('body').append(io.$container);